  display: flex;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 10; }
  .controls__btn {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: .5rem;
    padding: 1.4rem;
    border: none;
    border-radius: 4rem;
    background: #4e5a5f;
    transition: all 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    will-change: transform;
    color: white;
    overflow: hidden;
    cursor: pointer;
    outline: none; }
    .controls__btn svg {
      transition: all 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
      height: 1rem;
      width: 1rem;
      position: absolute; }
      .controls__btn svg:nth-child(2) {
        transform: translateY(2rem); }
      .controls__btn svg path {
        fill: white; }
    .controls__btn--toggled svg:nth-child(1) {
      transform: translateY(-2rem); }
    .controls__btn--toggled svg:nth-child(2) {
      transform: translateY(0rem); }
    .controls__btn:hover {
      transform: scale(1.1); }

@keyframes scaleIn {
  to {
    transform: scale(1); } }

@keyframes slideUpText {
  to {
    transform: translateY(0);
    opacity: 1; } }

.card {
  width: 9rem;
  height: 9rem;
  background: #fff;
  border-radius: 3px;
  position: absolute;
  margin: auto;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 9;
  display: flex; }
  .card h1 {
    font-family: system-ui, 'Helvetica Neue', Roboto, 'Open Sans', sans-serif;
    font-size: 1.5rem;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    letter-spacing: 0.07em;
    position: absolute;
    top: 100%;
    left: 0;
    color: #fff;
    font-weight: 200;
    text-align: center;
    margin: 1rem 0;
    width: 100%;
    overflow: hidden; }
    .card h1 span {
      opacity: 0;
      transform: translateY(100%);
      animation: slideUpText 1s 4.2s forwards cubic-bezier(1, 0, 0, 1);
      display: inline-block; }
      .card h1 span:nth-of-type(2) {
        animation-delay: 4.225s; }
      .card h1 span:nth-of-type(3) {
        animation-delay: 4.25s; }
      .card h1 span:nth-of-type(4) {
        animation-delay: 4.275s; }
      .card h1 span:nth-of-type(5) {
        animation-delay: 4.3s; }
  .card, .card img {
    transform: scale(0);
    animation: scaleIn 0.8s forwards cubic-bezier(1, 0, 0, 1);
    animation-delay: 3.7s; }
  .card img {
    animation-delay: 3.8s;
    margin: auto;
    border-radius: 50%; }

.bg-line,
.dot, .wrapper, .logo-mkbhd, .skewed-box .bg {
  position: absolute;
  left: 0;
  top: 0; }

.stripe {
  width: 1px;
  background: #fff;
  opacity: .55;
  position: absolute; }

.skewed-box .stripe {
  height: 170%;
  bottom: -40%;
  animation: scaleTop .6s both linear; }
  .skewed-box .stripe.top {
    transform-origin: 50% 100%; }
  .skewed-box .stripe.bottom {
    transform-origin: 50% 0; }

.skewed-box.white .stripe.top {
  right: 0;
  animation-delay: .2s; }

.skewed-box.white .stripe.topBottom {
  animation-name: scaleBottom;
  transform-origin: 50% 0;
  right: 6px;
  animation-delay: .3s; }

.skewed-box.white .stripe.bottomTop {
  transform-origin: 50% 100%;
  left: 6px;
  animation-delay: .4s; }

.skewed-box.white .stripe.bottom {
  animation-name: scaleBottom;
  animation-delay: 0; }

.skewed-box.pink .stripe.top {
  animation-delay: .5s; }

.skewed-box.pink .stripe.bottom {
  animation-name: scaleBottom;
  animation-delay: .4s;
  left: 100%; }

.final-stripe {
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  margin: auto;
  height: 30%;
  width: 1px; }
  .final-stripe.right {
    transform: rotateZ(60deg) translateY(-7rem);
    animation: finalStripeScaleRight .4s 3s both ease; }
  .final-stripe.left {
    animation: finalStripeScaleLeft .4s 3s both ease; }

@keyframes finalStripeScaleLeft {
  0% {
    transform: rotateZ(60deg) scaleY(0) translateY(7rem); }
  50% {
    transform: rotateZ(60deg) scaleY(1) translateY(7rem); }
  100% {
    transform: rotateZ(60deg) scaleY(1) translateY(14rem);
    opacity: 0; } }

@keyframes finalStripeScaleRight {
  0% {
    transform: rotateZ(60deg) scaleY(0) translateY(-7rem); }
  50% {
    transform: rotateZ(60deg) scaleY(1) translateY(-7rem); }
  100% {
    transform: rotateZ(60deg) scaleY(1) translateY(-14rem);
    opacity: 0; } }

@keyframes scaleBottom {
  0% {
    transform: translateY(0) scaleY(0); }
  50% {
    transform: translateY(0) scaleY(1); }
  100% {
    transform: translateY(100%) scaleY(0); } }

@keyframes scaleTop {
  0% {
    transform: translateY(0) scaleY(0); }
  50% {
    transform: translateY(0) scaleY(1); }
  100% {
    transform: translateY(-100%) scaleY(0); } }

.bg-line,
.dot {
  height: 1px;
  will-change: transform; }

.bg-line {
  width: 30px;
  background: #7a7e7f;
  animation: bgLine 2.8s infinite ease-in-out; }

.dot {
  width: 1px;
  background: #b6b9ba;
  border-radius: 50%;
  animation: dot 2.8s infinite ease-in-out; }

.bg-line:nth-child(1),
.dot:nth-child(1) {
  top: calc((0 * 100%) / 9 + 50% / 9);
  left: calc(0 * 100% / 12);
  animation-delay: -1.26s; }

.dot:nth-child(1) {
  left: calc(0 * 100% / 12 + 24.2px); }

.bg-line:nth-child(2),
.dot:nth-child(2) {
  top: calc((0 * 100%) / 9 + 50% / 9);
  left: calc(1 * 100% / 12);
  animation-delay: -2.34s; }

.dot:nth-child(2) {
  left: calc(1 * 100% / 12 + 9.8px); }

.bg-line:nth-child(3),
.dot:nth-child(3) {
  top: calc((0 * 100%) / 9 + 50% / 9);
  left: calc(2 * 100% / 12);
  animation-delay: -2.28s; }

.dot:nth-child(3) {
  left: calc(2 * 100% / 12 + 10.6px); }

.bg-line:nth-child(4),
.dot:nth-child(4) {
  top: calc((0 * 100%) / 9 + 50% / 9);
  left: calc(3 * 100% / 12);
  animation-delay: -0.18s; }

.dot:nth-child(4) {
  left: calc(3 * 100% / 12 + 38.6px); }

