// This is only used for the mute button and restarting the animation.
// If you remove this code the animation should work as well.
var audio = document.querySelector('audio');
var wrapper = document.querySelector('.scene');
var btnAudio = document.querySelector('#btn-audio');
var btnRestart = document.querySelector('#btn-restart');

btnRestart.addEventListener('click', function () {
  wrapper.style.display = 'none';
  setTimeout(function () {
    wrapper.style.display = 'block';
    audio.currentTime = 0;
    audio.play();
  }, 20);
});

btnAudio.addEventListener('click', function () {
  var elClass = 'controls__btn--toggled';
  btnAudio.classList.remove(elClass);
  audio.muted = !audio.muted;

  if (audio.muted) {
    btnAudio.classList.add(elClass);
  }
});"},"cmOpts":{"tabSize":2,"indentWithTabs":false},"ui":{"refreshType":"live-css","updateDelay":0},"editors":{"html":{"headContent":"<meta name="viewport" content="width=device-width, initial-scale=1">","htmlClasses":"","lang":"pug","contentLength":"3427<meta name="viewport" content="width=device-width, initial-scale=1">0"},"css":{"autoprefix":false,"libs":[],"lang":"scss","contentLength":14413},"js":{"libs":[],"lang":"babel","contentLength":749}}}

