.absolute-center {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  margin: auto;
}

.section {
  position: relative;
  width: 100%;
  min-height: 100vh;
}

.section--intro {
  overflow: hidden;
}

html, body {
  width: 100%;
  min-height: 100%;
  background: #f0f0f0;
  overflow-x: hidden;
}

.wrapper {
  width: 960px;
  height: 100%;
  margin: auto;
  z-index: 14;
  position: relative;
}

.square-wrapper {
  @extend .absolute-center;
  width: 200px;
  height: 200px;
  background: #f0f0f0;
}

.line {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 4px;
  background: introBg;
  &:nth-child(3) {
    top: auto;
    bottom: 0;
  }
  &:nth-child(2), &:nth-child(4) {
    top: 50%;
    left: -50%;
    margin-top: -2px;
    transform: rotate(90deg);
  }
  &:nth-child(2) {
    left: 50%;
  }
}

.square-dark-wrapper {
  @extend .absolute-center;
  width: 0;
  height: 0;
  will-change: transform;
  transform: scale(0);
}

.square-dark {
  background: introBg;
  border-radius: 50%;
  transform: translateX(-50%) translateY(-50%);
}

.sparks {
  @extend .absolute-center;
  width: 100px;
  height: 100%;
}

.spark {
  transform: rotate(45deg);
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  right: -200%;
  will-change: stroke-dashoffset;
  stroke-dasharray: 145;
  stroke-dashoffset: -145;
  &:last-child {
    left: -200%;
    right: auto;
    transform: rotate(-135deg);
  }
}

.pango-logo-group {
  @extend .absolute-center;
  margin-top: -5rem;
  z-index: 15;
}

.pango-logo {
  @extend .absolute-center;
  width: 190px;
  height: 190px;
  z-index: 16;
  opacity: 0;
  transform: scale(.9);
}

.pango-words {
  width: 190px;
  height: 120px;
  font-family: 'Lato';
  font-size: 3rem;
  font-weight: 300;
  color: #f0f0f0;
  @extend .absolute-center;
  top: auto;
  bottom: 10%;
  overflow: hidden;
  will-change: opacity;

  .line-separator {
    width: 90%;
    height: 1px;
    background: #f0f0f0;
    will-change: transform;
    margin: .2em auto;
  }
}

.pango-word {
  text-align: center;
  display: block;
  overflow: hidden;
  line-height: 1;
  span {
    will-change: transform;
    display: inline-block;
  }
  &:last-of-type {
    letter-spacing: 0.11em;
    font-size: 1.4rem;
    font-weight: 100;
  }
}

.outer-logo-circle {
  @extend .absolute-center;
  width: 190px;
  height: 190px;
  z-index: 14;
  transform-origin: 50% 50%;
  transform: rotate(-90deg) scale(.9);
  .circle {
    transform: translateZ(1px);
    stroke: introBg;
    stroke-dasharray: 2072;
    stroke-dashoffset: 2072;
    stroke-width: 16px;
  }
}

.bg-lines-wrapper {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 10;
  will-change: transform;
  transform: perspective(1000px) rotateX(45deg);
  transform-style: preserve-3d;
  .line {
    position: absolute;
    top: 0;
    left: 0;
    background: rgba(255,255,255,.2);
    width: 2px; 
    height: 2px;
    transform: scaleX(0);
    transform-origin: 0 50%;
  }
}

.mouse-tracker-layer {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 18;
}

.top-nav {
  position: absolute;
  top: 0;
  left: 0;
  z-index: 19;
}

.nav-list {
  list-style: none;
  padding: 1rem 2rem;
}

.nav-list--item {
  display: inline-block;
  a {
    color: #f0f0f0;
    padding: .4rem;
    margin: 0 .4rem;
    display: block;
    text-transform: uppercase;
    text-decoration: none;
    transition: all .2s ease;
    border: 1px solid transparent;
    &:hover {
      border-color: currentColor;
    }
  }
}","js":"// Replaces the element text with an array of spans
// and returns it
function splitTextInSpans(elem) {
  const letters = elem.textContent.split('')
  elem.innerHTML = ''
  return letters.map(letter => {
    const span = document.createElement('span')
    span.textContent = letter
    elem.appendChild(span)
    return span
  })
}

function intro() {
  function animateLogo(tl) {
    const maxWindowSize = Math.max(window.innerHeight, window.innerWidth)

    const squareDarkWrapper = document.querySelector('.square-dark-wrapper')
    const squareDark = squareDarkWrapper.querySelector('.square-dark')

    const square = document.querySelector('.square-wrapper')
    const lines = square.querySelectorAll('.line')

    const sparks = document.querySelectorAll('.spark')
    const outerCircle = document.querySelector('.outer-logo-circle .circle')
    const logo = document.querySelector('.pango-logo')

    tl.set(squareDark, {
        height: maxWindowSize,
        width: maxWindowSize
      })
      .set(lines, { scaleX: 0 })

    _.forEach(lines, (line, i) => {
      tl.to(lines[i], 2, {
        scaleX: 1,
        ease: 'Expo.easeOut'
      }, i / 10)
    })

    tl.to(lines, .05, {
        opacity: 0
      }, 1)
      .to(squareDarkWrapper, 1.5, {
        scale: 1.4,
        ease: 'Expo.easeOut'
      }, 1)
      .to(square, 2, {
        rotationZ: 45,
        ease: 'Expo.easeOut'
      }, 1.5)
      .to(sparks, 1, {
        strokeDashoffset: 0,
        ease: 'Expo.easeOut'
      }, 1.5)
      .to(sparks, 1, {
        strokeDashoffset: 145,
        ease: 'Expo.easeOut'
      }, 1.5)
      .to(outerCircle, 1.4, {
        strokeDashoffset: 0,
        ease: 'Expo.easeInOut'
      }, 1.7)
      .to(outerCircle, .4, {
        opacity: 0,
        ease: 'Power4.easeOut'
      }, 2.7)
      .to(logo, 1, {
        opacity: 1,
        scale: 1,
        ease: 'Power4.easeOut'
      }, 2.7)

    return tl
  }

  function animateWords(tl) {
    const wordsWrapper  = document.querySelector('.pango-words')
    const words         = wordsWrapper.querySelectorAll('.pango-word')
    const lineSeparator = wordsWrapper.querySelector('.line-separator')

    tl.set(lineSeparator, { scaleX: 0 })
      .to(lineSeparator, 1, {
        scaleX: 1,
        ease: 'Expo.easeOut'
      }, 3)

    _.forEach(words, word => {
      const spans = splitTextInSpans(word)
      _.forEach(spans, (span, j) => {
        let tlWords = new TimelineMax()

        tlWords.set(span, { y: span.clientHeight, opacity: 1 })
        const delay = (3 + ((Math.abs(spans.length / 2 - j)) / 20)).toFixed(2)
        tlWords.to(span, 1.4, {
          y: 0,
          ease: 'Expo.easeOut'
        }, delay)
      })
    })
    return tl
  }

  function animateBackground(tl) {
    const wrapper = document.querySelector('.bg-lines-wrapper')
    const createLines = qty => {
      for (let i = 0; i < qty; i++) {
        const line = document.createElement('div')
        line.classList.add('line')
        wrapper.appendChild(line)
        tl.set(line, {
            x: _.random(window.innerWidth),
            y: _.random(window.innerHeight),
            z: _.random(-200, 200),
            width: _.random(0, 100) + 50,
            scaleX: 0
          })
      }
    }

    function animateLines(lines) {
      for (let line of lines) {
        const startTime = _.random(5, true)
        tl.to(line, .4, {
            scaleX: 1,
            ease: 'Expo.Power4'
          }, startTime)
          .to(line, 2, {
            scaleX: 0,
            x: '+=' + line.style.width,
            ease: 'Power4.easeOut'
          }, startTime + .4)
      }

      // 3d rotation
      const rotate = (rx, ry) => {
        TweenMax.to(wrapper, .75, {
            rotationX: ry,
            rotationY: rx,
            ease: 'Power0.easenone'
          })
      }

      // update rotation values
      function update(elem, event, tilt) {
        if (tilt) {
          const tiltLR = event.gamma
          const tiltFB = event.beta
          rotate(tiltLR / 1.5, tiltFB / 1.5)

          return
        }

        const xpos = event.layerX || event.offsetX
        const ypos = event.layerY || event.offsetY

        const ax = -(window.innerWidth / 2 - xpos) / 40
        const ay = (window.innerHeight / 2 - ypos) / 10
        rotate(ax, ay)
      }

      // get element for mousemove event tracking
      // on top of all other layers
      const trackerLayer = document.querySelector('.mouse-tracker-layer')
      trackerLayer.addEventListener('mousemove', e => window.requestAnimationFrame(() => update(wrapper, e)))

      if (window.DeviceOrientationEvent) {
        window.addEventListener('deviceorientation', e =>
          window.requestAnimationFrame(() => update(wrapper, e, true)))
      }
    }

    createLines(50)
    animateLines(wrapper.querySelectorAll('.line'))
  }

  return {
    animateLogo,
    animateWords,
    animateBackground
  }
}

